import React, { useState, useEffect } from 'react';

import './Profile.css';

const Profile = ( { token } ) => {

    const [profile, setProfile] = useState('');

    useEffect(() => {

        const fetchUser = async () => {
            try {
                const response = await fetch('https://strangers-things.herokuapp.com/api/2206-FTB-ET-WEB-FT-B/users/me', {

                headers: {
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${token}`
                  }, 

                });

                const data = await response.json();
                console.log(data);
                console.log("this is the username:", data.data.username);
                    setProfile(data.data);
                } catch (error) {
                    console.error(error);
            }

    };

    fetchUser();

    }, []);

return (
    <div className="user-profile">
            {
            <h2> {
                profile.length ? profile.map((currentUser, idx) => {

            return <div key={idx}>
                    <div className ="indivPost">   
                        <p>{currentUser.username}</p>
                        <p>{currentUser.messages}</p>
                        <p>{currentUser.posts}</p>
                    </div> 

                    </div>
                    }) : <div>You aren't logged in!</div>
                    }

                </h2>      
                }
        </div>      

)
};

export default Profile;
