import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';

import './Posts.css';

const Posts = ( {setPostId} ) => {

    const [posts, setPosts] = useState([]);
    const navigate = useNavigate()
    const [searchInput, setSearchInput] = useState("");


    useEffect(() => {

        const fetchPosts = async () => {
            try {
                const response = await fetch('https://strangers-things.herokuapp.com/api/2206-FTB-ET-WEB-FT-B/posts');
                const data = await response.json();
                console.log(data);
                console.log(data.data);
                    setPosts(data.data.posts);
                } catch (error) {
                    console.error(error);
            }

    };

    fetchPosts();

    }, []);

    function view (event) {
      setPostId(event.target.id)  
      navigate(`/Messages/${event.target.value}`)
      // console.log(event.target.value)

  }

  const searchItems = (searchValue) => {
    setSearchInput(searchValue)
    // currentPost.title.filter((item) => {
        // return Object.values(item).join('').toLowerCase().includes(searchInput.toLowerCase())

}

    return (
        <div>
            <input type="text" placeholder="Search"
            onChange={(event) => searchItems(event.target.value)}/>

        <div className="user-posts">
            {
                <h2> {
                posts.length ? posts.map((currentPost, idx) => {

                    return <div key={idx}>
                    <div className ="indivPost">   
                        <p>{currentPost.title}</p>
                        <p>{currentPost.description}</p>
                        <p>{currentPost.author.username}</p>
                        <button value={currentPost._id} onClick={view}>Edit</button>
                        <button value={currentPost.author.username} onClick={view}>Message User</button>

                    </div> 

                    </div>
                    }) : <div>No posts to display!</div>
                    }

                </h2>    



            }
        </div>      
        </div>


            )

}



export default Posts;
